package com.zimi.zimixing.bean;/** * Created by tanjun on 2018/1/16. * 弹出框选项 */public class CardBean{    private String id;    private String name;    public CardBean(String id, String name) {        this.id = id;        this.name = name;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }}